#!/usr/bin/env node
/**
 * Audio File Server
 * Serves audio files generated by clawdbot AI
 */

const http = require('http');
const fs = require('fs');
const path = require('path');

const PORT = 8766;

const server = http.createServer((req, res) => {
  // Enable CORS
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET, OPTIONS');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

  if (req.method === 'OPTIONS') {
    res.writeHead(200);
    res.end();
    return;
  }

  if (req.method === 'GET' && req.url.startsWith('/audio')) {
    // Extract file path from URL
    // URL format: /audio?path=/var/folders/.../file.mp3
    const queryString = req.url.split('?')[1];
    if (!queryString) {
      res.writeHead(400);
      res.end(JSON.stringify({ error: 'No query string' }));
      return;
    }
    const urlParams = new URLSearchParams(queryString);
    const filePath = urlParams.get('path');

    if (!filePath) {
      res.writeHead(400);
      res.end(JSON.stringify({ error: 'No file path provided' }));
      return;
    }

    console.log('ðŸŽµ Serving audio file:', filePath);

    // Check if file exists
    if (!fs.existsSync(filePath)) {
      console.error('âŒ File not found:', filePath);
      res.writeHead(404);
      res.end(JSON.stringify({ error: 'File not found' }));
      return;
    }

    // Read and serve file
    fs.readFile(filePath, (err, data) => {
      if (err) {
        console.error('âŒ Error reading file:', err);
        res.writeHead(500);
        res.end(JSON.stringify({ error: 'Failed to read file' }));
        return;
      }

      // Determine content type
      const ext = path.extname(filePath).toLowerCase();
      const contentType = {
        '.mp3': 'audio/mpeg',
        '.wav': 'audio/wav',
        '.ogg': 'audio/ogg',
        '.m4a': 'audio/mp4'
      }[ext] || 'audio/mpeg';

      res.writeHead(200, {
        'Content-Type': contentType,
        'Content-Length': data.length
      });
      res.end(data);

      console.log('âœ… Audio file served:', path.basename(filePath));
    });

  } else {
    res.writeHead(404);
    res.end('Not found');
  }
});

server.listen(PORT, '127.0.0.1', () => {
  console.log(`ðŸŽµ Audio File Server running on http://127.0.0.1:${PORT}`);
  console.log(`ðŸ“‚ Serving audio files from AI-generated responses`);
  console.log(`ðŸ’¡ Example: http://127.0.0.1:${PORT}/audio?path=/path/to/file.mp3`);
});
